/*global app, $on */

import todo from "./components/todo.js";
import helpers from "./components/helpers.js";
/**
 * Sets up a brand new Todo list.
 *
 * @param {string} name The name of your new to do list.
 */
(function() {
	function setView() {
		todo.controller.setView(document.location.hash);
	}
	setView();

	helpers.$on(window, "load", setView);
	helpers.$on(window, "hashchange", setView);
})();

/**
 * Takes a model and view and acts as the controller between them.
 *
 * @example
 * let myController = new MyController(model, view);
 */
export default class Controller {
	/**
	 *
	 * @constructor
	 * @param {object} model The model instance
	 * @param {object} view The view instance
	 */
	constructor(model, view) {
		/**
		 * @type {Object} model
		 */
		this.model = model;
		/**
		 * @type {Object} view
		 */
		this.view = view;
		this.addItem = this.addItem.bind(this);
		this.view.bind("newTodo", title => {
			this.addItem(title);
		});
		this.view.bind("itemEdit", item => {
			this.editItem(item.id);
		});
		this.view.bind("itemEditDone", item => {
			this.editItemSave(item.id, item.title);
		});
		this.view.bind("itemEditCancel", item => {
			this.editItemCancel(item.id);
		});
		this.view.bind("itemRemove", item => {
			this.removeItem(item.id);
		});

		this.view.bind("itemToggle", item => {
			this.toggleComplete(item.id, item.completed);
		});

		this.view.bind("removeCompleted", () => {
			this.removeCompletedItems();
		});

		this.view.bind("toggleAll", status => {
			this.toggleAll(status.completed);
		});
	}
	/**
	 * An event to fire whenever you want to add an item. Simply pass in the event
	 * object and it'll handle the DOM insertion and saving of the new item.
	 *
	 * @param {string} title The title of the new todo to add
	 */
	addItem(title) {
		if (title.trim() === "") {
			return;
		}

		this.model.create(title, () => {
			this.view.render("clearNewTodo");
			this._filter(true);
		});
	}
	/**
	 * Triggers the item editing mode.
	 *
	 * @param {number} id The id of the todo to edite
	 */
	editItem(id) {
		this.model.read(id, data => {
			this.view.render("editItem", { id: id, title: data[0].title });
		});
	}
	/**
	 * Finishes the item editing mode successfully.
	 *
	 * @param {number} id The id of the save todo edite
	 * @param {string} title Todo's title
	 */
	editItemSave(id, title) {
		title = title.trim();

		if (title.length !== 0) {
			this.model.update(id, { title: title }, () => {
				this.view.render("editItemDone", { id: id, title: title });
			});
		} else {
			this.removeItem(id);
		}
	}
	/**
	 * Cancels the item editing mode.
	 *
	 * @param {number} id The id of the todo to cancel the editing
	 */
	editItemCancel(id) {
		this.model.read(id, data => {
			this.view.render("editItemDone", { id: id, title: data[0].title });
		});
	}
	/**
	 * By giving it an ID it'll find the DOM element matching that ID,
	 * remove it from the DOM and also remove it from storage.
	 *
	 * @param {number} id The ID of the item to remove from the DOM and storage.
	 */
	removeItem(id) {
		this.model.remove(id, () => {
			this.view.render("removeItem", id);
		});

		this._filter();
	}
	/**
	 * Will remove all completed items from the DOM and storage.
	 */
	removeCompletedItems() {
		this.model.read({ completed: true }, data => {
			data.forEach(item => {
				this.removeItem(item.id);
			});
		});

		this._filter();
	}
	/**
	 * Give it an ID of a model and it will update the item
	 * in storage based on the checkbox's state.
	 *
	 * @param {number} id The ID of the element to complete or uncomplete
	 * @param {object} completed To check the state of complete or not
	 * @param {boolean|undefined} silent Prevent re-filtering the todo items
	 */
	toggleComplete(id, completed, silent) {
		this.model.update(id, { completed: completed }, () => {
			this.view.render("elementComplete", {
				id: id,
				completed: completed
			});
		});

		if (!silent) {
			this._filter();
		}
	}
	/**
	 * Will toggle ALL checkboxes' on/off state and completeness of models
	 * Just pass in the event object.
	 *
	 * @param {boolean|undefined} completed State of the toggleAll checkbox
	 */
	toggleAll(completed) {
		this.model.read({ completed: !completed }, data => {
			data.forEach(item => {
				this.toggleComplete(item.id, completed, true);
			});
		});

		this._filter();
	}
	/**
	 * Updates the pieces of the page which change depending on the remaining
	 * number of todos.
	 *
	 * @private
	 */
	_updateCount() {
		this.model.getCount(todos => {
			this.view.render("updateElementCount", todos.active);
			this.view.render("clearCompletedButton", {
				completed: todos.completed,
				visible: todos.completed > 0
			});

			this.view.render("toggleAll", {
				checked: todos.completed === todos.total
			});
			this.view.render("contentBlockVisibility", {
				visible: todos.total > 0
			});
		});
	}
	/**
	 * Re-filters the todo items, based on the active route.
	 *
	 * @param {boolean|undefined} force  forces a re-painting of todo items.
	 * @private
	 */
	_filter(force) {
		let activeRoute =
			this._activeRoute.charAt(0).toUpperCase() + this._activeRoute.substr(1);

		// Update the elements on the page, which change with each completed todo
		this._updateCount();

		// If the last active route isn't "All", or we're switching routes, we
		// re-create the todo item elements, calling:
		//   this.show[All|Active|Completed]();
		if (
			force ||
			this._lastActiveRoute !== "All" ||
			this._lastActiveRoute !== activeRoute
		) {
			this["show" + activeRoute]();
		}
		/**
		 * Store a reference to the last active route, allowing us to add condition and not show the new active route if it's the same as the last active route.
		 *
		 * @type {string} _lastActiveRoute
		 */
		this._lastActiveRoute = activeRoute;
	}
	/**
	 * Simply updates the filter nav's selected states.
	 *
	 * @param {string} currentPage
	 * @private
	 */
	_updateFilterState(currentPage) {
		/**
		 * Store a reference to the active route, allowing us to re-filter todo items as they are marked complete or incomplete.
		 *
		 * @type {string} _activeRoute
		 */
		this._activeRoute = currentPage;

		if (currentPage === "") {
			this._activeRoute = "All";
		}

		this._filter();

		this.view.render("setFilter", currentPage);
	}
	/**
	 * Loads and initialises the view.
	 *
	 * @param {string} '' | 'active' | 'completed'
	 */
	setView(locationHash) {
		let route = locationHash.split("/")[1];
		let page = route || "";
		this._updateFilterState(page);
	}

	/**
	 * An event to fire on load. Will get all items and display them in the
	 * todo-list
	 */
	showAll() {
		this.model.read(data => {
			this.view.render("showEntries", data);
		});
	}

	/**
	 * Renders all active tasks
	 */
	showActive() {
		this.model.read({ completed: false }, data => {
			this.view.render("showEntries", data);
		});
	}

	/**
	 * Renders all completed tasks
	 */
	showCompleted() {
		this.model.read({ completed: true }, data => {
			this.view.render("showEntries", data);
		});
	}
}

/*global NodeList */

/**
 * Manage the events with the DOM through the methods defined
 *
 * @example
 * let myHelpers = new MyHelpers();
 */
class Helpers {
	/**
	 * Act as a querySelector event
	 *
	 * @param {string} selector Represents the class selector to be reach
	 * @param {undefined|object} scope Represente the scope on which to look for the DOM's class selector
	 * @returns {object} Return the element from querySelector
	 */
	qs(selector, scope) {
		return (scope || document).querySelector(selector);
	}

	/**
	 * Act as a querySelectorAll event
	 *
	 * @param {string} selector Represents the element/class DOM selector to be reach
	 * @param {undefined|object} scope Represente the scope on which to look for the DOM's class selector
	 * @returns {object} Return the elements from querySelectorAll
	 */
	qsa(selector, scope) {
		return (scope || document).querySelectorAll(selector);
	}
	/**
	 * Build an event listener according to the arguments passed in
	 *
	 * @param {object} target Represents the targeted HTML tag from the DOM
	 * @param {string} type Represents the type of event
	 * @param {function} callback Represent the callback function attach to this event
	 * @param {boolean} useCapture
	 */
	$on(target, type, callback, useCapture) {
		target.addEventListener(type, callback, !!useCapture);
	}

	/**
	 * Create an event listener on a target element
	 *
	 * @param {object} target Represents the targeted HTML tag from the DOM
	 * @param {string} selector Represents the element/class DOM selector to be reach
	 * @param {string} type Represents the type of event
	 * @param {function} handler Represent the callback function attach to this event
	 */
	$delegate(target, selector, type, handler) {
		self = this;
		function dispatchEvent(event) {
			let targetElement = event.target;
			let potentialElements = self.qsa(selector, target);
			let hasMatch =
				Array.prototype.indexOf.call(potentialElements, targetElement) >= 0;

			if (hasMatch) {
				handler.call(targetElement, event);
			}
		}

		// https://developer.mozilla.org/en-US/docs/Web/Events/blur
		let useCapture = type === "blur" || type === "focus";

		this.$on(target, type, dispatchEvent, useCapture);
	}
	/**
	 * Reach the parent node of a given element
	 *
	 * @param {object} element Represents the element HTML tag that has been clicked from the DOM
	 * @param {string} tagName The tag name to be reach
	 * @return {object} Return the parentNode of a specific tag name (if exist).
	 */
	$parent(element, tagName) {
		if (!element.parentNode) {
			return;
		}
		if (element.parentNode.tagName.toLowerCase() === tagName.toLowerCase()) {
			return element.parentNode;
		}
		return this.$parent(element.parentNode, tagName);
	}
}

NodeList.prototype.forEach = Array.prototype.forEach;

let helpers = new Helpers();

export default helpers;

/**
 * Creates a new Model instance and hooks up the storage.
 *
 * @example
 * let myModel = new MyModel(storage);
 */
export default class Model {
	/**
	 *
	 * @constructor
	 * @param {object} storage A reference to the client side storage class
	 */
	constructor(storage) {
		/**
		 * @type {Object} Represente the locaStorage which store all the todos
		 */
		this.storage = storage;
	}
	/**
	 * Creates a new todo model.
	 *
	 * @param {string} [title] The title of the task
	 * @param {function} [callback] The callback to fire after the model is created
	 */
	create(title, callback) {
		title = title || "";
		callback = callback || function() {};

		let newItem = {
			title: title.trim(),
			completed: false
		};

		this.storage.save(newItem, callback);
	}

	/**
	 * Finds and returns a model in storage. If no query is given it'll simply
	 * return everything. If you pass in a string or number it'll look that up as
	 * the ID ofthe model to find. Lastly, you can pass it an object to match
	 * against.
	 *
	 * @param {string|number|object} [query] A query to match models against
	 * @param {function} [callback] The callback to fire after the model is found
	 * @return {Array} Return an array of parsed todos.
	 *
	 * @example
	 * model.read(1, func); // Will find the model with an ID of 1
	 * model.read('1'); // Same as above
	 * //Below will find a model with foo equalling bar and hello equalling world.
	 * model.read({ foo: 'bar', hello: 'world' });
	 */
	read(query, callback) {
		let queryType = typeof query;
		callback = callback || function() {};

		if (queryType === "function") {
			callback = query;
			return this.storage.findAll(callback);
		} else if (queryType === "string" || queryType === "number") {
			query = parseInt(query, 10);
			this.storage.find({ id: query }, callback);
		} else {
			this.storage.find(query, callback);
		}
	}
	/**
	 * Updates a model by giving it an ID, data to update, and a callback to fire when
	 * the update is complete.
	 *
	 * @param {number} id The id of the model to update
	 * @param {object} data The properties to update and their new value
	 * @param {function} callback The callback to fire when the update is complete
	 */
	update(id, data, callback) {
		this.storage.save(data, callback, id);
	}

	/**
	 * Removes a model from storage.
	 *
	 * @param {number} id The ID of the model to remove
	 * @param {function} callback The callback to fire when the removal is complete
	 */
	remove(id, callback) {
		this.storage.remove(id, callback);
	}

	/**
	 * WARNING: Will remove ALL data from storage.
	 * @ignore
	 * @param {function} callback The callback to fire when the storage is wiped.
	 */
	removeAll(callback) {
		this.storage.drop(callback); // TODO indicate that it's not use
	}

	/**
	 * Returns a count of all todos.
	 * @param {function} callback The callback to fire with todos Object when the getCount function is finished
	 */
	getCount(callback) {
		let todos = {
			active: 0,
			completed: 0,
			total: 0
		};

		this.storage.findAll(data => {
			data.forEach(function(todo) {
				if (todo.completed) {
					todos.completed++;
				} else {
					todos.active++;
				}

				todos.total++;
			});
			callback(todos);
		});
	}
}

